import React, { useState, useEffect } from "react";
import TodoDataService from "../services/todo.service";

const TodoList = (props) => {
  const initialTodoState = {
    id: null,
    task: "",
  };
  const [todo, setTodo] = useState(initialTodoState);
  const [todos, setTodos] = useState([]);
  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    fetchTodos();
  }, []);

  const handleInputChange = event => {
    const { name, value } = event.target;
    setTodo({ ...todo, [name]: value });
  };

  const fetchTodos = () => {
    TodoDataService.getAll()
      .then(response => {
        setTodos(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  };

  const saveTodo = () => {
    const { task } = todo;

    if (editMode) {
      TodoDataService.update(todo.id, { task: task })
        .then(response => {
          fetchTodos();
          setEditMode(false);
          setTodo(initialTodoState);
        })
        .catch(e => {
          console.log(e);
        });
    } else {
      TodoDataService.create({ task: task })
        .then(response => {
          fetchTodos();
          setTodo(initialTodoState);
        })
        .catch(e => {
          console.log(e);
        });
    }
  };

  const editTodo = todo => {
    setTodo(todo);
    setEditMode(true);
  };

  const removeTodo = id => {
    TodoDataService.remove(id)
      .then(response => {
        fetchTodos();
      })
      .catch(e => {
        console.log(e);
      });
  };

  return (
    <div className="todo-list">
      <h4>Todo List</h4>
      <ul>
        {todos.map(t => (
          <li key={t.id}>
            {t.task}
            <button onClick={() => editTodo(t)} className="btn btn-primary">
              Edit
            </button>
            <button onClick={() => removeTodo(t.id)} className="btn btn-danger">
              Delete
            </button>
          </li>
        ))}
      </ul>
      <div className="todo-form">
        <div className="form-group">
          <label htmlFor="task">Task</label>
          <input
            type="text"
            className="form-control"
            id="task"
            required
            value={todo.task}
            onChange={handleInputChange}
            name="task"
          />
        </div>

        <button onClick={saveTodo} className="btn btn-success">
          {editMode ? "Update" : "Add"}
        </button>
      </div>
    </div>
  );
};

export default TodoList;
